mapper.select:
WITH RECURSIVE SEARCH_NAV(SEQ, DOMAIN_CD, TITLE, ORDER_SEQ, PARENT_SEQ, URL, ICON, MENU_GROUP, MENU_GROUP_ORDER, RMK, IS_USE, DELT_FLG, CRE_ID, CRE_DT, UPD_ID, UPD_DT, LEVEL, PATH, CYCLE) AS ( SELECT M.SEQ, M.DOMAIN_CD, M.TITLE, M.ORDER_SEQ, M.PARENT_SEQ, M.URL, M.ICON, M.TITLE, M.ORDER_SEQ, M.RMK, M.IS_USE, M.DELT_FLG, M.CRE_ID, M.CRE_DT, M.UPD_ID, M.UPD_DT, 0, ARRAY[M.SEQ], FALSE FROM NAV_MENU M WHERE M.PARENT_SEQ = 0
AND M.DOMAIN_CD = #{domain_cd}
UNION ALL SELECT M.SEQ, M.DOMAIN_CD, M.TITLE, M.ORDER_SEQ, M.PARENT_SEQ, M.URL, M.ICON, SN.TITLE, SN.ORDER_SEQ, M.RMK, M.IS_USE, M.DELT_FLG, M.CRE_ID, M.CRE_DT, M.UPD_ID, M.UPD_DT, LEVEL+1, PATH||M.SEQ, M.SEQ = ANY(PATH) FROM NAV_MENU M, SEARCH_NAV SN WHERE SN.SEQ = M.PARENT_SEQ
AND M.DOMAIN_CD = #{domain_cd}
AND NOT CYCLE )
SELECT SEQ, DOMAIN_CD, TITLE, PARENT_SEQ, URL, ICON, MENU_GROUP, MENU_GROUP_ORDER, ORDER_SEQ, level, RMK, IS_USE, DELT_FLG, CRE_ID, CRE_DT, UPD_ID, UPD_DT FROM SEARCH_NAV ORDER BY MENU_GROUP_ORDER, level, ORDER_SEQ
